sp_widget.script:
(function() {
    /* populate the 'data' object */
    /* e.g., data.table = $sp.getValue('table'); */
      var userThemeGR = new GlideRecord('u_m2m_u_dynamic_theme_sys_user');
      userThemeGR.setLimit(1);
      userThemeGR.addQuery('sys_user', gs.getUserID());

      userThemeGR.query()

      if(userThemeGR.next()){
        data.theme.brand_primary = userThemeGR.u_dynamic_theme.u_brand_primary.getValue();
        data.theme.brand_secondary = userThemeGR.u_dynamic_theme.u_brand_secondary.getValue();
        data.theme.brand_neutral = userThemeGR.u_dynamic_theme.u_brand_neutral.getValue();
      }
  })();
